import React, {Component} from 'react';
import Counter from './counter';

class Counters extends Component {
    state = {
        xya: [
            { id: 1, value:55},
            { id: 2, value:80},
            { id: 3, value:10},
            { id: 4, value:34},
            { id: 5, value:28},
        ]
    };
    render() {
        return (
            <div>

                {this.state.xya.map( x =>
                    <Counter key={x.id}  value={x.value} selected={true}/>)}

            </div>
        );
    }
}

export default Counters;
