import React, {Component} from 'react';
import Counter from './counter';

class Counters extends Component {
    state = {
        counter: [
            {id: 1, value: 4 },
            {id: 2, value: 0 },
            {id: 3, value: 0 },
            {id: 4, value: 0 },

        ]
    };

    handleDelete = (id) => {
        const newcounter = this.state.counter.filter(c => c.id !== id);
        this.setState({counter: newcounter});
    }

    render() {
        return (
            <div>

                {this.state.counterprop.map(x => (

                    <Counter key={x.id} counterprop={counter} onDelete={this.handleDelete}>
                        <h6>Counter #{x.id}</h6>
                    </Counter>


                    ))}

            </div>
        );
    }
}

export default Counters;
