import React, {Component} from 'react';

class Counter extends Component {

    getBadgeClass() {
        let classes = "badge m-2 badge-";
        classes += this.state.value > 0 ? "primary" : "warning";
        return classes;
    };


    formatValue() {
        const {value} = this.state;
        return value > 0 ? value : "zero"
    }


    handleIncrement = () => {
        this.setState({value: this.state.value + 1});
        console.log('new value is',this.state.value + 1)
    };

    handleDecrease = () => {
        this.setState({value: this.state.value -1});
        console.log('new value is',this.state.value -1 )
    };




    render() {

        console.log('props', this.props);


        return (
            <div>

                {this.props.children}
                <span className={this.getBadgeClass()}>{this.formatValue()}</span>

                <button
                    className="btn btn-secondary btn-sm"
                    onClick={ this.handleIncrement }>Increment
                </button>

                <button
                    onClick={ this.handleDecrease }
                    className="btn btn-secondary btn-sm m-2">Decrease</button>

                <button
                    onClick={ () =>this.props.onDelete(this.props.counterproperty.id) }
                    className="btn btn-danger btn-sm m-2">Delete</button>

                <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>

            </div>
        );
    }


}

export default Counter;
