import React, {Component} from 'react';
import Counter from './counter';

class Counters extends Component {
    state = {
        counterprop: [
            {id: 1, value: 4},
            {id: 2, value: 0},
            {id: 3, value: 9},
            {id: 4, value: 18},

        ]
    };

    handleDelete = (id) => {
        const newcounter = this.state.counterprop.filter(c => c.id !== id);
        this.setState({counterprop: newcounter});
        console.log('one item is deleted', id)
    };

    handleReset = () => {
        const newcounter = this.state.counterprop.map(c => {
            c.value = 0;
            return c;
            });
        this.setState({counterprop: newcounter})

}
    render() {
        return (
            <div>

                <button
                    className='badge badge-light m-xl-5'
                    onClick={this.handleReset}


                >Reset</button>

                {this.state.counterprop.map(x => (
                    <Counter key={x.id}
                            counter={this.counterprop}
                             onDelete={this.handleDelete}>
                        <h6>Counter #{x.id}</h6>
                    </Counter>


                ))}

            </div>
        );
    }
}

export default Counters;
